mapper.select:
select contentsno, cateno,pname, price, filename, stock from contents
pname like concat('%', #{word} ,'%')
price like concat('%', #{word} ,'%')
and cateno = #{cateno}
order by contentsno desc limit #{sno}, #{eno}

select contentsno, cateno,pname, price, filename, stock,rdate from contents
pname like concat('%', #{word} ,'%')
price like concat('%', #{word} ,'%')
cateno = #{word}
order by contentsno desc limit #{sno}, #{eno}

mapper.update:
update contents
set pname =#{pname},
price = #{price},
detail = #{detail}
where contentsno =#{contentsno}

update contents
set
filename = #{filename}
where contentsno = #{contentsno}
